using System;
using System.Diagnostics;
using System.Numerics;

namespace _8
{
    class Program
    {
        static int maxNum = 13;
        //Предпологается, что в строке есть хотя бы один 0
        static string sequence = "";
        static int numTry = 50;

        static void Main(string[] args)
        {
            for (int i = 0; i < numTry; i++)
                calculating();
            Console.ReadKey();
        }

        static void calculating()
        {
            Stopwatch sw = Stopwatch.StartNew();

            BigInteger max = 0;

            for (int i= 0; i < (sequence.Length - 1) - (maxNum - 1); i++)
            {
                BigInteger num = BigInteger.Parse(sequence[i].ToString());
                for (BigInteger j = 1; j < maxNum; j++)
                    num *= BigInteger.Parse(sequence[i+Convert.ToInt32(j.ToString())].ToString());
                if (num > max)
                    max = num;
            }

            Console.WriteLine(max + " (" + sw.ElapsedMilliseconds + "ms)");
            sw.Stop();
        }
    }
}
